import java.util.HashMap;

public class Judge {
    public static void main(String[] args) {
        String test = "";
        HashMap<Character, Integer> letterMap = new HashMap<>();
        for (char lol : test.toCharArray()) {
            letterMap.put(lol, letterMap.getOrDefault(lol,0) + 1);
        }
        boolean answer = (letterMap.getOrDefault('U',0).equals(letterMap.getOrDefault('D', 0))
                && letterMap.getOrDefault('L', 0).equals(letterMap.getOrDefault('R', 0)) &&
                 letterMap.size() != 0) ? true : false;
        System.out.println(answer);
    }
}
